import React from "react";

export default function CaptionComp({ data, onCopyClick }) {
  return (
    <div className="caption-component">
      <div style={{ lineHeight: 2, fontSize: 13 }}>{data.text}</div>

      <button
        onClick={() => {
          onCopyClick();
        }}
        className="copy-button"
      >
        COPY
      </button>
    </div>
  );
}
