import React from "react";
import { CopyToClipboard } from "react-copy-to-clipboard";

export default function CaptionComp({ data, onCopyClick }) {
  return (
    <div className="caption-component">
      <div style={{ lineHeight: 2, fontSize: 13 }}>{data.text}</div>
      <CopyToClipboard text={data.text}>
        <button
          onClick={() => {
            onCopyClick();
          }}
          className="copy-button"
        >
          COPY
        </button>
      </CopyToClipboard>
    </div>
  );
}
