import { useState } from "react";

import "./App.css";
import CaptionComp from "./components/CaptionComp";
import SingleTitle from "./components/SingleTitle";
import { Constants } from "./config";
import useWindowDimensions from "./services/useWindowDimensions";
import { BallTriangle } from "react-loader-spinner";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  const notify = () => toast("Caption Copied");
  const [captionData, setCaptionData] = useState(Constants.DATA);
  const [isLoading, setIsLoading] = useState(false);
  const [titleType, setTitleType] = useState("trending");
  const titleData = ["trending", "love", "nature", "friendship", "travel"];

  const onTitleClick = (value) => {
    setTitleType(value);
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      setCaptionData(
        value === "trending"
          ? Constants.DATA
          : value === "love"
          ? Constants.DATA2
          : value === "nature"
          ? Constants.DATA3
          : value === "friendship"
          ? Constants.DATA4
          : Constants.DATA5
      );
    }, "1000");
  };

  const { width, height } = useWindowDimensions();

  return (
    <div>
      <ToastContainer
        // toastStyle={{ backgroundColor: "#DFFF00" }}
        bodyClassName="toast-text"
        toastClassName="toast-text2"
        progressClassName="progress-color"
        position="top-right"
        autoClose={5000}
        hideProgressBar={true}
        // progressStyle={{ color: "red", backgroundColor: "red" }}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />

      <div>
        {/* header */}
        <div>
          <div className="header">Caption Generator</div>
        </div>

        {/* caption-category */}
        <div style={{ marginTop: 50, margin: 50, flexDirection: "row" }}>
          {titleData.map((item) => (
            <SingleTitle
              text={"#" + item}
              filled={titleType === item}
              onClick={() => {
                onTitleClick(item);
              }}
            />
          ))}
        </div>

        {/* caption-container */}

        <div style={{ height: height - 220 }} className="caption-container">
          {isLoading === true ? (
            <div style={{ height: height - 220 }} className="loader-style">
              <BallTriangle
                color="#c8ee90"
                height={height - 450}
                width={width - 400}
              />
            </div>
          ) : (
            captionData.map((item) => (
              <CaptionComp data={item} onCopyClick={notify} />
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
