import { useState } from "react";
import Tabs, { Tab } from "react-best-tabs";
import "react-best-tabs/dist/index.css";
import "./App.css";
import SingleTitle from "./components/SingleTitle";

function App() {
  const [titleType, setTitleType] = useState("trending");
  const titleData = ["trending", "love", "nature", "friendship", "travel"];

  const onTitleClick = (value) => {
    console.log("hello");
    console.log(titleType, "title- type");
    setTitleType(value);
  };

  return (
    <div>
      {/* header */}
      <div
        style={{
          backgroundColor: "#B1BC55",
          margin: 0,
          height: 50,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          color: "white",
        }}
      >
        <div>Caption Generator</div>
      </div>

      {/* caption-category */}
      <div style={{ marginTop: 50, margin: 50, flexDirection: "row" }}>
        {titleData.map((item) => (
          <SingleTitle
            text={item}
            filled={titleType == item}
            onClick={() => {
              onTitleClick(item);
            }}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
