import { useState } from "react";
import Tabs, { Tab } from "react-best-tabs";
import "react-best-tabs/dist/index.css";
import "./App.css";
import CaptionComp from "./components/CaptionComp";
import SingleTitle from "./components/SingleTitle";
import { Constants } from "./config";
import useWindowDimensions from "./services/useWindowDimensions";
import { BallTriangle } from "react-loader-spinner";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  const notify = () => toast("Caption Copied!");
  const [captionData, setCaptionData] = useState(Constants.DATA);
  const [isLoading, setIsLoading] = useState(false);
  const [titleType, setTitleType] = useState("trending");
  const titleData = ["trending", "love", "nature", "friendship", "travel"];

  const onTitleClick = (value) => {
    setTitleType(value);
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      setCaptionData(
        value == "trending"
          ? Constants.DATA
          : value == "love"
          ? Constants.DATA2
          : value == "nature"
          ? Constants.DATA3
          : value == "friendship"
          ? Constants.DATA4
          : Constants.DATA5
      );
    }, "1000");
  };

  const { width, height } = useWindowDimensions();

  return (
    <div>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      {/* header */}
      <div>
        <div className="header">Caption Generator</div>
      </div>

      {/* caption-category */}
      <div style={{ marginTop: 50, margin: 50, flexDirection: "row" }}>
        {titleData.map((item) => (
          <SingleTitle
            text={"#" + item}
            filled={titleType == item}
            onClick={() => {
              onTitleClick(item);
              notify();
            }}
          />
        ))}
      </div>

      {/* caption-container */}

      <div style={{ height: height - 220 }} className="caption-container">
        {isLoading == true ? (
          <div style={{ height: height - 220 }} className="loader-style">
            <BallTriangle
              color="#c8ee90"
              height={height - 450}
              width={width - 400}
            />
          </div>
        ) : (
          captionData.map((item) => <CaptionComp data={item} />)
        )}
      </div>
    </div>
  );
}

export default App;
